mapper.select:
select * from
user
where 1=1

	and userName=#{userName}


	and password=#{password}

 		select u.*,s.name sceDisName from user u,scenic_district s where u.sceDisId=s.id and u.userName!='admin'

			and u.userName like'%${userName}%'


			and s.name like'%${sceDisName}%'


			and u.role=#{role}

 		 limit #{start},#{rows}
